import React from 'react';
import { Outlet } from 'react-router-dom';
import ProfilePicture from './danielescobar.jpg';
const imageStyles = { minWidth: 300, minHeight: 300 };



const IndexPage = () => {
  return (
    <main className='flex h-screen'>
            <aside className='bg-blue-600 hidden lg:flex w-80'>
              
              <img src={ProfilePicture} alt="Logo" />;
              <h1 className='text-white text-3xl font-bold'>Daniel Escobar</h1>

              </aside>
            <div className='w-full h-screen flex flex-col'>
                
                <div className='flex-grow'>
                    <Outlet />
                </div>
                <footer className='lg:hidden'>Footer</footer>
            </div>
        </main>
  );
};

export { IndexPage };
